	////////////////////////////////////////////////////////////////////////////////

	// Instantiate the NeedItUtils class.  Call the isDatePast method and pass
	// the u_when_needed value.

	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);

    // Call the isDateToday method in order to see if the dates meet the lead time
	// pass the u_when_needed value

	var isDateToday = niutils.isDateToday(current.u_when_needed);

	// If the isDatePast method returns true, the date is in the past
	if(isPast == true){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

	// If isDateToday returns true, the date is today and lead times are
	// not being met

	else if(isDateToday == true){
		gs.addErrorMessage("When needed date needs to be at least one day from now in order to meet lead times");
		current.setAbortAction(true);
	}

})(current, previous);
